import React, { useState } from "react";
import Setting from "./components/Setting";
import Form from "./components/Form";

function App() {
  const [budget, handleBudget] = useState(0);

  const [remainingBudget, handleRemaining] = useState(0);

  const [showSetting, handleSetting] = useState(true);

  const [newExpense, handleNewExpense] = useState([]);

  return (
    <div className="container">
      <header>
        <h1>Weekly expenses</h1>
        <div className="contenido-principal contenido">
          {showSetting ? (
            <Setting
              handleBudget={handleBudget}
              handleRemaining={handleRemaining}
              handleSetting={handleSetting}
            />
          ) : (
            <div className="row">
              <div className="one-half column">
                <Form handleNewExpense={handleNewExpense} />
              </div>
              <div className="one-half column">2</div>
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
